def get_all_categories():
    """Return list of all vocabulary categories"""
    return list(VOCABULARY_DATA.keys())

def get_vocabulary_by_category(category):
    """Return vocabulary items for a specific category"""
    return VOCABULARY_DATA.get(category, [])

def get_random_word(category=None):
    """Get a random word, optionally from a specific category"""
    import random
    if category and category in VOCABULARY_DATA:
        return random.choice(VOCABULARY_DATA[category])
    else:
        all_words = [word for words in VOCABULARY_DATA.values() for word in words]
        return random.choice(all_words)
